     1→<!DOCTYPE html>
     2→<html lang="zh-CN">
     3→<head>
     4→    <meta charset="UTF-8">
     5→    <meta name="viewport" content="width=device-width, initial-scale=1.0">
     6→    <title>学校学生管理系统</title>
     7→    <style>
     8→        * {
     9→            margin: 0;
    10→            padding: 0;
    11→            box-sizing: border-box;
    12→        }
    13→
    14→        body {
    15→            font-family: 'Microsoft YaHei', Arial, sans-serif;
    16→            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    17→            min-height: 100vh;
    18→            padding: 20px;
    19→        }
    20→
    21→        .container {
    22→            max-width: 1400px;
    23→            margin: 0 auto;
    24→            background: white;
    25→            border-radius: 20px;
    26→            box-shadow: 0 20px 40px rgba(0,0,0,0.1);
    27→            overflow: hidden;
    28→        }
    29→
    30→        .header {
    31→            background: linear-gradient(45deg, #4CAF50, #45a049);
    32→            color: white;
    33→            text-align: center;
    34→            padding: 30px 20px;
    35→        }
    36→
    37→        .header h1 {
    38→            font-size: 32px;
    39→            margin-bottom: 10px;
    40→        }
    41→
    42→        .header p {
    43→            opacity: 0.9;
    44→            font-size: 16px;
    45→        }
    46→
    47→        .nav-tabs {
    48→            display: flex;
    49→            background: #f8f9fa;
    50→            border-bottom: 1px solid #ddd;
    51→        }
    52→
    53→        .nav-tab {
    54→            flex: 1;
    55→            padding: 20px;
    56→            text-align: center;
    57→            background: none;
    58→            border: none;
    59→            font-size: 16px;
    60→            font-weight: 500;
    61→            cursor: pointer;
    62→            transition: all 0.3s ease;
    63→            color: #666;
    64→        }
    65→
    66→        .nav-tab.active {
    67→            background: white;
    68→            color: #4CAF50;
    69→            border-bottom: 3px solid #4CAF50;
    70→        }
    71→
    72→        .nav-tab:hover {
    73→            background: #e9ecef;
    74→        }
    75→
    76→        .tab-content {
    77→            display: none;
    78→            padding: 30px;
    79→        }
    80→
    81→        .tab-content.active {
    82→            display: block;
    83→        }
    84→
    85→        .section-title {
    86→            font-size: 24px;
    87→            color: #333;
    88→            margin-bottom: 20px;
    89→            display: flex;
    90→            align-items: center;
    91→            gap: 10px;
    92→        }
    93→
    94→        .controls {
    95→            display: flex;
    96→            gap: 15px;
    97→            margin-bottom: 30px;
    98→            flex-wrap: wrap;
    99→            align-items: center;
   100→        }
   101→
   102→        .search-box {
   103→            flex: 1;
   104→            min-width: 250px;
   105→            position: relative;
   106→        }
   107→
   108→        .search-box input {
   109→            width: 100%;
   110→            padding: 12px 15px 12px 40px;
   111→            border: 2px solid #ddd;
   112→            border-radius: 25px;
   113→            font-size: 16px;
   114→            outline: none;
   115→            transition: border-color 0.3s ease;
   116→        }
   117→
   118→        .search-box input:focus {
   119→            border-color: #4CAF50;
   120→        }
   121→
   122→        .search-icon {
   123→            position: absolute;
   124→            left: 15px;
   125→            top: 50%;
   126→            transform: translateY(-50%);
   127→            color: #999;
   128→        }
   129→
   130→        .btn {
   131→            background: #007bff;
   132→            color: white;
   133→            border: none;
   134→            padding: 12px 20px;
   135→            border-radius: 25px;
   136→            font-size: 14px;
   137→            cursor: pointer;
   138→            transition: all 0.3s ease;
   139→            white-space: nowrap;
   140→        }
   141→
   142→        .btn:hover {
   143→            transform: translateY(-2px);
   144→            box-shadow: 0 5px 15px rgba(0,0,0,0.2);
   145→        }
   146→
   147→        .btn-primary { background: #007bff; }
   148→        .btn-success { background: #28a745; }
   149→        .btn-warning { background: #ffc107; color: #212529; }
   150→        .btn-danger { background: #dc3545; }
   151→
   152→        .filter-select {
   153→            padding: 12px 15px;
   154→            border: 2px solid #ddd;
   155→            border-radius: 25px;
   156→            font-size: 14px;
   157→            outline: none;
   158→            background: white;
   159→        }
   160→
   161→        .student-grid {
   162→            display: grid;
   163→            grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
   164→            gap: 20px;
   165→            margin-top: 20px;
   166→        }
   167→
   168→        .student-card {
   169→            background: white;
   170→            border: 1px solid #ddd;
   171→            border-radius: 15px;
   172→            padding: 20px;
   173→            box-shadow: 0 4px 8px rgba(0,0,0,0.1);
   174→            transition: all 0.3s ease;
   175→        }
   176→
   177→        .student-card:hover {
   178→            transform: translateY(-5px);
   179→            box-shadow: 0 8px 20px rgba(0,0,0,0.15);
   180→        }
   181→
   182→        .student-header {
   183→            display: flex;
   184→            justify-content: space-between;
   185→            align-items: center;
   186→            margin-bottom: 15px;
   187→            padding-bottom: 15px;
   188→            border-bottom: 2px solid #f0f0f0;
   189→        }
   190→
   191→        .student-name {
   192→            font-size: 20px;
   193→            font-weight: bold;
   194→            color: #333;
   195→        }
   196→
   197→        .student-id {
   198→            background: #4CAF50;
   199→            color: white;
   200→            padding: 4px 12px;
   201→            border-radius: 15px;
   202→            font-size: 12px;
   203→            font-weight: 500;
   204→        }
   205→
   206→        .student-info {
   207→            display: grid;
   208→            grid-template-columns: 1fr 1fr;
   209→            gap: 10px;
   210→        }
   211→
   212→        .info-item {
   213→            display: flex;
   214→            flex-direction: column;
   215→        }
   216→
   217→        .info-label {
   218→            font-size: 12px;
   219→            color: #666;
   220→            margin-bottom: 4px;
   221→            font-weight: 500;
   222→        }
   223→
   224→        .info-value {
   225→            font-size: 14px;
   226→            color: #333;
   227→            font-weight: 500;
   228→        }
   229→
   230→        .card-actions {
   231→            margin-top: 15px;
   232→            display: flex;
   233→            gap: 10px;
   234→            justify-content: flex-end;
   235→        }
   236→
   237→        .btn-sm {
   238→            padding: 6px 12px;
   239→            font-size: 12px;
   240→            border-radius: 15px;
   241→        }
   242→
   243→        .modal {
   244→            display: none;
   245→            position: fixed;
   246→            top: 0;
   247→            left: 0;
   248→            width: 100%;
   249→            height: 100%;
   250→            background: rgba(0,0,0,0.5);
   251→            z-index: 1000;
   252→            align-items: center;
   253→            justify-content: center;
   254→        }
   255→
   256→        .modal.active {
   257→            display: flex;
   258→        }
   259→
   260→        .modal-content {
   261→            background: white;
   262→            border-radius: 20px;
   263→            padding: 30px;
   264→            width: 90%;
   265→            max-width: 600px;
   266→            max-height: 90vh;
   267→            overflow-y: auto;
   268→            box-shadow: 0 20px 40px rgba(0,0,0,0.2);
   269→        }
   270→
   271→        .modal-header {
   272→            display: flex;
   273→            justify-content: space-between;
   274→            align-items: center;
   275→            margin-bottom: 25px;
   276→            padding-bottom: 15px;
   277→            border-bottom: 2px solid #f0f0f0;
   278→        }
   279→
   280→        .modal-title {
   281→            font-size: 24px;
   282→            color: #333;
   283→        }
   284→
   285→        .close-btn {
   286→            background: none;
   287→            border: none;
   288→            font-size: 24px;
   289→            cursor: pointer;
   290→            color: #999;
   291→            padding: 0;
   292→            width: 30px;
   293→            height: 30px;
   294→            display: flex;
   295→            align-items: center;
   296→            justify-content: center;
   297→        }
   298→
   299→        .form-grid {
   300→            display: grid;
   301→            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
   302→            gap: 20px;
   303→            margin-bottom: 25px;
   304→        }
   305→
   306→        .form-group {
   307→            display: flex;
   308→            flex-direction: column;
   309→        }
   310→
   311→        .form-group label {
   312→            font-weight: 500;
   313→            color: #333;
   314→            margin-bottom: 8px;
   315→            font-size: 14px;
   316→        }
   317→
   318→        .form-group input,
   319→        .form-group select,
   320→        .form-group textarea {
   321→            padding: 12px 15px;
   322→            border: 2px solid #ddd;
   323→            border-radius: 10px;
   324→            font-size: 14px;
   325→            outline: none;
   326→            transition: border-color 0.3s ease;
   327→            font-family: inherit;
   328→        }
   329→
   330→        .form-group input:focus,
   331→        .form-group select:focus,
   332→        .form-group textarea:focus {
   333→            border-color: #4CAF50;
   334→        }
   335→
   336→        .form-group textarea {
   337→            resize: vertical;
   338→            min-height: 60px;
   339→        }
   340→
   341→        .modal-actions {
   342→            display: flex;
   343→            gap: 15px;
   344→            justify-content: flex-end;
   345→            margin-top: 25px;
   346→            padding-top: 20px;
   347→            border-top: 1px solid #eee;
   348→        }
   349→
   350→        .stats-grid {
   351→            display: grid;
   352→            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
   353→            gap: 20px;
   354→            margin-bottom: 30px;
   355→        }
   356→
   357→        .stat-card {
   358→            background: linear-gradient(135deg, #4CAF50, #45a049);
   359→            color: white;
   360→            padding: 25px;
   361→            border-radius: 15px;
   362→            text-align: center;
   363→            box-shadow: 0 4px 8px rgba(0,0,0,0.1);
   364→        }
   365→
   366→        .stat-number {
   367→            font-size: 36px;
   368→            font-weight: bold;
   369→            margin-bottom: 8px;
   370→        }
   371→
   372→        .stat-label {
   373→            font-size: 14px;
   374→            opacity: 0.9;
   375→        }
   376→
   377→        .schedule-container {
   378→            background: #f8f9fa;
   379→            border-radius: 15px;
   380→            padding: 20px;
   381→            margin-top: 20px;
   382→        }
   383→
   384→        .schedule-controls {
   385→            display: flex;
   386→            gap: 15px;
   387→            margin-bottom: 20px;
   388→            align-items: center;
   389→            flex-wrap: wrap;
   390→        }
   391→
   392→        .schedule-table {
   393→            width: 100%;
   394→            border-collapse: collapse;
   395→            background: white;
   396→            border-radius: 10px;
   397→            overflow: hidden;
   398→            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
   399→        }
   400→
   401→        .schedule-table th,
   402→        .schedule-table td {
   403→            padding: 12px 15px;
   404→            text-align: center;
   405→            border-bottom: 1px solid #eee;
   406→        }
   407→
   408→        .schedule-table th {
   409→            background: #4CAF50;
   410→            color: white;
   411→            font-weight: 500;
   412→            font-size: 14px;
   413→        }
   414→
   415→        .schedule-table td {
   416→            font-size: 13px;
   417→            cursor: pointer;
   418→            transition: background-color 0.3s ease;
   419→        }
   420→
   421→        .schedule-table td:hover {
   422→            background: #f0f8ff;
   423→        }
   424→
   425→        .time-slot {
   426→            background: #e9ecef;
   427→            font-weight: 500;
   428→            color: #495057;
   429→        }
   430→
   431→        .course-cell {
   432→            position: relative;
   433→            min-height: 50px;
   434→        }
   435→
   436→        .course-item {
   437→            background: #007bff;
   438→            color: white;
   439→            padding: 4px 8px;
   440→            border-radius: 5px;
   441→            font-size: 12px;
   442→            margin: 2px 0;
   443→            line-height: 1.2;
   444→        }
   445→
   446→        @media (max-width: 768px) {
   447→            .container {
   448→                margin: 10px;
   449→                border-radius: 15px;
   450→            }
   451→
   452→            .nav-tab {
   453→                padding: 15px 10px;
   454→                font-size: 14px;
   455→            }
   456→
   457→            .tab-content {
   458→                padding: 20px;
   459→            }
   460→
   461→            .controls {
   462→                flex-direction: column;
   463→                align-items: stretch;
   464→            }
   465→
   466→            .search-box {
   467→                min-width: 100%;
   468→            }
   469→
   470→            .student-grid {
   471→                grid-template-columns: 1fr;
   472→            }
   473→
   474→            .form-grid {
   475→                grid-template-columns: 1fr;
   476→            }
   477→
   478→            .stats-grid {
   479→                grid-template-columns: repeat(2, 1fr);
   480→            }
   481→
   482→            .schedule-table {
   483→                font-size: 12px;
   484→            }
   485→
   486→            .schedule-table th,
   487→            .schedule-table td {
   488→                padding: 8px 4px;
   489→            }
   490→        }
   491→    </style>
   492→</head>
   493→<body>
   494→    <div class="container">
   495→        <div class="header">
   496→            <h1>🏫 学校学生管理系统</h1>
   497→            <p>学生信息统计与课程表管理</p>
   498→        </div>
   499→
   500→        <div class="nav-tabs">
   501→            <button class="nav-tab active" onclick="switchTab('students')">📋 学生信息管理</button>
   502→            <button class="nav-tab" onclick="switchTab('schedule')">📅 课程表管理</button>
   503→        </div>
   504→
   505→        <!-- 学生信息管理页面 -->
   506→        <div id="students" class="tab-content active">
   507→            <h2 class="section-title">📊 学生信息统计</h2>
   508→
   509→            <div class="stats-grid">
   510→                <div class="stat-card">
   511→                    <div class="stat-number" id="totalStudents">0</div>
   512→                    <div class="stat-label">总学生数</div>
   513→                </div>
   514→                <div class="stat-card" style="background: linear-gradient(135deg, #FF6B6B, #EE5A24);">
   515→                    <div class="stat-number" id="gradeCount">0</div>
   516→                    <div class="stat-label">年级数量</div>
   517→                </div>
   518→                <div class="stat-card" style="background: linear-gradient(135deg, #4ECDC4, #44A08D);">
   519→                    <div class="stat-number" id="classCount">0</div>
   520→                    <div class="stat-label">班级数量</div>
   521→                </div>
   522→                <div class="stat-card" style="background: linear-gradient(135deg, #45B7D1, #96C93D);">
   523→                    <div class="stat-number" id="dormCount">0</div>
   524→                    <div class="stat-label">宿舍楼数</div>
   525→                </div>
   526→            </div>
   527→
   528→            <div class="controls">
   529→                <div class="search-box">
   530→                    <span class="search-icon">🔍</span>
   531→                    <input type="text" id="searchInput" placeholder="搜索学生姓名、班级或宿舍..." onkeyup="filterStudents()">
   532→                </div>
   533→                <select class="filter-select" id="gradeFilter" onchange="filterStudents()">
   534→                    <option value="">所有年级</option>
   535→                </select>
   536→                <select class="filter-select" id="classFilter" onchange="filterStudents()">
   537→                    <option value="">所有班级</option>
   538→                </select>
   539→                <button class="btn btn-success" onclick="showAddStudentModal()">➕ 添加学生</button>
   540→                <button class="btn btn-warning" onclick="exportData()">📤 导出数据</button>
   541→                <button class="btn btn-primary" onclick="importData()">📥 导入数据</button>
   542→            </div>
   543→
   544→            <div class="student-grid" id="studentGrid">
   545→                <!-- 学生卡片将在这里动态生成 -->
   546→            </div>
   547→        </div>
   548→
   549→        <!-- 课程表管理页面 -->
   550→        <div id="schedule" class="tab-content">
   551→            <h2 class="section-title">📅 课程表管理</h2>
   552→
   553→            <div class="schedule-container">
   554→                <div class="schedule-controls">
   555→                    <select class="filter-select" id="scheduleGrade" onchange="loadSchedule()">
   556→                        <option value="">选择年级</option>
   557→                    </select>
   558→                    <select class="filter-select" id="scheduleClass" onchange="loadSchedule()">
   559→                        <option value="">选择班级</option>
   560→                    </select>
   561→                    <button class="btn btn-success" onclick="showAddCourseModal()">➕ 添加课程</button>
   562→                    <button class="btn btn-primary" onclick="generateScheduleTemplate()">📋 生成模板</button>
   563→                </div>
   564→
   565→                <table class="schedule-table" id="scheduleTable">
   566→                    <thead>
   567→                        <tr>
   568→                            <th>时间</th>
   569→                            <th>周一</th>
   570→                            <th>周二</th>
   571→                            <th>周三</th>
   572→                            <th>周四</th>
   573→                            <th>周五</th>
   574→                            <th>周六</th>
   575→                            <th>周日</th>
   576→                        </tr>
   577→                    </thead>
   578→                    <tbody id="scheduleBody">
   579→                        <!-- 课程表内容将在这里动态生成 -->
   580→                    </tbody>
   581→                </table>
   582→            </div>
   583→        </div>
   584→    </div>
   585→
   586→    <!-- 添加/编辑学生模态框 -->
   587→    <div id="studentModal" class="modal">
   588→        <div class="modal-content">
   589→            <div class="modal-header">
   590→                <h3 class="modal-title" id="studentModalTitle">添加学生</h3>
   591→                <button class="close-btn" onclick="closeModal('studentModal')">×</button>
   592→            </div>
   593→
   594→            <form id="studentForm">
   595→                <div class="form-grid">
   596→                    <div class="form-group">
   597→                        <label>姓名 *</label>
   598→                        <input type="text" id="studentName" required>
   599→                    </div>
   600→                    <div class="form-group">
   601→                        <label>年龄</label>
   602→                        <input type="number" id="studentAge" min="6" max="25">
   603→                    </div>
   604→                    <div class="form-group">
   605→                        <label>年级 *</label>
   606→                        <select id="studentGrade" required>
   607→                            <option value="">选择年级</option>
   608→                            <option value="一年级">一年级</option>
   609→                            <option value="二年级">二年级</option>
   610→                            <option value="三年级">三年级</option>
   611→                            <option value="四年级">四年级</option>
   612→                            <option value="五年级">五年级</option>
   613→                            <option value="六年级">六年级</option>
   614→                            <option value="初一">初一</option>
   615→                            <option value="初二">初二</option>
   616→                            <option value="初三">初三</option>
   617→                            <option value="高一">高一</option>
   618→                            <option value="高二">高二</option>
   619→                            <option value="高三">高三</option>
   620→                        </select>
   621→                    </div>
   622→                    <div class="form-group">
   623→                        <label>班级 *</label>
   624→                        <input type="text" id="studentClass" placeholder="如：1班、2班" required>
   625→                    </div>
   626→                    <div class="form-group">
   627→                        <label>联系电话</label>
   628→                        <input type="tel" id="studentPhone">
   629→                    </div>
   630→                    <div class="form-group">
   631→                        <label>宿舍楼</label>
   632→                        <input type="text" id="studentDorm" placeholder="如：A栋、B栋">
   633→                    </div>
   634→                </div>
   635→                <div class="form-group">
   636→                    <label>家庭住址</label>
   637→                    <textarea id="studentAddress" placeholder="请输入详细地址"></textarea>
   638→                </div>
   639→
   640→                <div class="modal-actions">
   641→                    <button type="button" class="btn btn-danger" onclick="closeModal('studentModal')">取消</button>
   642→                    <button type="submit" class="btn btn-success">保存</button>
   643→                </div>
   644→            </form>
   645→        </div>
   646→    </div>
   647→
   648→    <!-- 添加课程模态框 -->
   649→    <div id="courseModal" class="modal">
   650→        <div class="modal-content">
   651→            <div class="modal-header">
   652→                <h3 class="modal-title">添加课程</h3>
   653→                <button class="close-btn" onclick="closeModal('courseModal')">×</button>
   654→            </div>
   655→
   656→            <form id="courseForm">
   657→                <div class="form-grid">
   658→                    <div class="form-group">
   659→                        <label>年级 *</label>
   660→                        <select id="courseGrade" required>
