'use client';

import { useEffect, useState } from 'react';
import Link from 'next/link';

export default function Page() {
  const [info, setInfo] = useState<string>('');
  const [loading, setLoading] = useState(true);
  const [name, setName] = useState<string>('');
  const [personalMessage, setPersonalMessage] = useState<string>('');

  useEffect(() => {
    fetch('/api/info')
      .then((res) => res.json())
      .then((data) => {
        setInfo(data.data);
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
      });
  }, []);

  const handleSubmit = async () => {
    try {
      const response = await fetch('/api/personal-info', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name }),
      });

      const data = await response.json();

      if (response.ok) {
        setPersonalMessage(data.message);
      } else {
        setPersonalMessage(data.error);
      }
    } catch (error) {
      setPersonalMessage('请求失败，请稍后重试');
    }
  };

  return (
    <div className="container grid items-center gap-4 px-4 text-center md:px-6 lg:gap-10">
      <div className="space-y-3">
        <h1 className="text-4xl font-bold tracking-tighter sm:text-5xl md:text-6xl">
          Welcome to our Platform
        </h1>
        {loading ? (
          <p className="mx-auto max-w-[600px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed">
            加载中...
          </p>
        ) : (
          <p className="mx-auto max-w-[600px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed">
            {info}
          </p>
        )}
        {personalMessage && (
          <p className="mx-auto max-w-[600px] text-gray-700 font-semibold md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed">
            {personalMessage}
          </p>
        )}
      </div>
      <div className="flex flex-col justify-center gap-2 min-[400px]:flex-row">
        <input
          type="text"
          placeholder="请输入你的名字"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="inline-flex h-10 items-center justify-center rounded-md border border-gray-200 bg-white px-4 text-sm focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-gray-950"
        />
        <button
          onClick={handleSubmit}
          className="inline-flex h-10 items-center justify-center rounded-md border border-gray-200 bg-white px-8 text-sm font-medium shadow-sm transition-colors hover:bg-gray-100 hover:text-gray-900 focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-gray-950 disabled:pointer-events-none disabled:opacity-50"
        >
          提交
        </button>
        <Link
          className="inline-flex h-10 items-center justify-center rounded-md border border-gray-200 bg-white px-8 text-sm font-medium shadow-sm transition-colors hover:bg-gray-100 hover:text-gray-900 focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-gray-950 disabled:pointer-events-none disabled:opacity-50"
          href="/signup"
        >
          Sign up
        </Link>
      </div>
    </div>
  );
}

